def find_marker(stream: str, window = 4):
    for i in range(0, len(stream) - window):
        substr = stream[i:i + window]
        set_s = set(c for c in substr)
        if len(set_s) == window:
            return i + window

    return -1

s = ""

print(find_marker(s))
print(find_marker(s, 14))

